mapper.select:
SELECT COUNT(*) as cnt
FROM manager
WHERE manager_id=#{manager_id} AND manager_passwd=#{manager_passwd}

SELECT manager_no, manager_id, manager_passwd, manager_name, manager_date, manager_grade 
FROM manager
WHERE manager_id=#{manager_id}

SELECT manager_no, manager_id, manager_passwd, manager_name, manager_date, manager_grade
FROM manager
WHERE manager_no=#{manager_no}
