mapper.select:
SELECT COUNT(*) as cnt FROM pill
WHERE dno=#{dno}
WHERE dno=#{dno} AND (UPPER(pill_name) LIKE '%' || UPPER(#{search}) || '%' OR UPPER(efficacy) LIKE '%' || UPPER(#{search}) || '%' OR UPPER(side) LIKE '%' || UPPER(#{search}) || '%')

SELECT pill_no, manager_no, dno, pill_name, efficacy, side, views, passwd, search, rdate, file1, file1saved, thumb1, size1, r FROM ( SELECT pill_no, manager_no, dno, pill_name, efficacy, side, views, passwd, search, rdate, file1, file1saved, thumb1, size1, rownum as r FROM ( SELECT pill_no, manager_no, dno, pill_name, efficacy, side, views, passwd, search, rdate, file1, file1saved, thumb1, size1 FROM pill
WHERE dno=#{dno}
WHERE dno=#{dno} AND (UPPER(pill_name) LIKE '%' || UPPER(#{search}) || '%' OR UPPER(efficacy) LIKE '%' || UPPER(#{search}) || '%' OR UPPER(side) LIKE '%' || UPPER(#{search}) || '%')
ORDER BY pill_no DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM pill
WHERE pill_no = #{pill_no} AND passwd=#{passwd}

mapper.update:
UPDATE pill
SET pill_name=#{pill_name}, efficacy=#{efficacy},  side=#{side}, serach=#{search}
WHERE pill_no = #{pill_no}

UPDATE pill
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE pill_no = #{pill_no}

